mapper.select:
SELECT

FROM t_finance_bill t
WHERE t.id = #{id}

SELECT

FROM t_finance_bill t
WHERE t.del_flag = #{DEL_FLAG_NORMAL}

	AND t.title like concat(concat('%',#{title}),'%')


	AND t.bill_user = #{billUser}


	AND t.matter = #{matter}


	AND t.payment = #{payment}


	AND date_format(t.bill_time,'%Y-%m-%d') >= #{startBillTime}


	AND #{endBillTime} >= date_format(t.bill_time,'%Y-%m-%d')



		ORDER BY ${page.orderBy}


		ORDER BY t.create_date DESC

mapper.update:
UPDATE t_finance_bill SET bill_time = #{billTime}, amount_capital = #{amountCapital}, matter = #{matter}, amount = #{amount}, bill_user = #{billUser}, payment = #{payment}, operator = #{operator}, title = #{title},
del_flag = #{delFlag},
remarks = #{remarks} WHERE id = #{id}

UPDATE t_finance_bill SET
finance_status = #{financeStatus},
opinion = #{opinion}
WHERE id = #{id}
