mapper.select:
SELECT

FROM t_goods t
WHERE t.id = #{id}

SELECT

FROM t_goods t
WHERE t.del_flag = #{DEL_FLAG_NORMAL}

	AND t.goods_name like concat(concat('%',#{goodsName}),'%')



		ORDER BY ${page.orderBy}


		ORDER BY t.create_date DESC

mapper.update:
UPDATE t_goods SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
